import React from 'react'
import "./css/CardIndex.css"


const url = "http://localhost:3001/planos";
// const loadingElement = document.querySelector("#loading");
// const outputElement = document.querySelector("#output");

async function carregarProdutos() {
    loadingElement.style.display = "inline-block";
    const response = await fetch(url);
    const dadosJson = await response.json();
    // loadingElement.style.display = "none";
    outputElement.innerHTML = "";
    dadosJson.map((p) => {
    outputElement.innerHTML += `
        <div class="planCard-img"></div>
            <tr>
                    <td>${p.nome}</td>
                    <td>${p.descricao}</td>
                    <td>${p.preco.toLocaleString("pt-br", {
                    minimumFractionDigits: 2,
                    })}</td>
                    <td>
                        <a href="alterar.html?id=${p.id}">Alterar</a>
                        <button type="button" data-id="${p.id}" data-nome="${p.nome}"
                    </td>
                </tr>
        `;
    });


carregarProdutos();
}
