import React from "react";
import "./css/Cadastra.css";

const url = " http://localhost:3000/?nome=&email=";
const btnSalvar = document.querySelector("button");

async function salvarProduto() {
    const form = document.querySelector("form");
    const response = await fetch(url, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify({
            nome: form.nome.value,
            email: form.email.value,
        }),
    });
    return response.status === 201; //criação
}

btnSalvar.addEventListener("click", async(e) => {
    e.preventDefault();
    if (await salvarProduto()) {
        // location.assign("/index.html");
        alert("Produto salvo com sucesso!");
    } else {
        alert("Não foi possível salvar o produto!");
    }
});

const Cadastra = () => {
  return (
    <form id="cadastro" className="cadastro">
      <h1>newsletter</h1>
      <label>
        Nome:
        <input id="nome" name="nome" className="inpt" type="text" />
      </label>
      <label>
        E-mail:
        <input id="email" name="email" className="inpt" type="text" />
      </label>
      <button className="envia" type="button" value="Enviar">Salvar</button>
    </form>
  );
};

export default Cadastra;
