async function salvarProduto() {
    const form = document.querySelector("form");
    const response = await fetch(url, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify({
            nome: form.nome.value,
            descricao: form.descricao.value,
            preco: Number(form.preco.value.toString().replace(",", ".")),

        }),
    });
    return response.status === 201; //criação
}

const Cadastra = () => {
  return (
    <form id="cadastro" className="cadastro">
      <h1>newsletter</h1>
      <label>
        Nome:
        <input className="inpt" type="text" />
      </label>
      <label>
        E-mail:
        <input className="inpt" type="text" />
      </label>
      <input className="envia" type="submit" value="Enviar" />
    </form>
  );
};

export default Cadastra;
